import React, {useEffect, useState} from 'react';
import {Link, useNavigate} from "react-router-dom";
import ListView from '../listview'; // Import the GridView component
import axios from "axios";

const data11 = [
    {id: 1, name: 'Item 1', description: 'Description 1'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},
    {id: 2, name: 'Item 2', description: 'Description 2'},

    // Add more items as needed
];



function GatewayList() {
    const [gateways, setGateways] = useState([]);
    const [updatedGateways, setUpdatedGateways] = useState([]);

    const config = {
        headers: {
            "Content-Type": "application/json" // Use standard JSON content type
        }
    };
    useEffect(() => {
        const data = {
            admin_id: (localStorage.getItem('admin_id')).toString()
        };
        // Fetch gateways data from the backend API
        axios
            .post('http://localhost:8000/api/gateway-lists/', JSON.stringify(data), config)
            .then((response) => {
                setGateways(response.data);
            })
            .catch((error) => {
                console.error('Error fetching gateways:', error);
            });
    }, []);
    const handleInputChange = (index, field, value) => {
        const updated = [...updatedGateways];
        if (!updated[index]) updated[index] = {};
        updated[index][field] = value;
        setUpdatedGateways(updated);
    };

    const handleUpdateClick = (index) => {
        const updatedGateway = updatedGateways[index];
        if (updatedGateway) {
            // onUpdate(index, updatedGateway);
            // Clear the updated data after updating
            setUpdatedGateways([]);
        }
    };

    return (
        <div>
            <p style={{paddingLeft: 20}}>   User Logged IN: {localStorage.getItem("username")}</p>
            {gateways.map((gateway, index) => (
                <div key={index}>
                    <span>Antenna Diameter: {gateway.antenna_diameter}</span>
                    <input
                        type="text"
                        placeholder="New Antenna Diameter"
                        value={updatedGateways[index]?.antenna_diameter || ''}
                        onChange={(e) => handleInputChange(index, 'antenna_diameter', e.target.value)}
                    />
                    <span>Location Name: {gateway.location_name}</span>
                    <input
                        type="text"
                        placeholder="New Location Name"
                        value={updatedGateways[index]?.location_name || ''}
                        onChange={(e) => handleInputChange(index, 'location_name', e.target.value)}
                    />
                    <span>Latitude: {gateway.latitude}</span>
                    <input
                        type="text"
                        placeholder="New Latitude"
                        value={updatedGateways[index]?.latitude || ''}
                        onChange={(e) => handleInputChange(index, 'latitude', e.target.value)}
                    />
                    <span>Longitude: {gateway.longitude}</span>
                    <input
                        type="text"
                        placeholder="New Longitude"
                        value={updatedGateways[index]?.longitude || ''}
                        onChange={(e) => handleInputChange(index, 'longitude', e.target.value)}
                    />
                    <button onClick={() => handleUpdateClick(index)}>Update</button>
                </div>
            ))}
        </div>
    );
}


export default GatewayList;
