import logo from './logo.svg';
import './App.css';

import React, {useEffect, useState} from "react";

import {BrowserRouter as Router, Switch, Route, useNavigate} from 'react-router-dom';
import axios from "axios";

function LoginForm({userType}) {
    const [username, setUsername] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    const data = {
        username: username
    };

    const config = {
        headers: {
            "Content-Type": "application/json" // Use standard JSON content type
        }
    };
    const getPlc = () => {
        switch (userType) {
            case 'Admin':
                return `Admin Username`;
            case 'Operator':
                return `Operator Username`;
            default:
                return `Administrator`;
        }
    };

    const geth2title = () => {
        switch (userType) {
            case 'Admin':
                return `Administrator Login`;
            case 'Operator':
                return `Operator Login`;
            default:
                return `Create`;
        }
    };
    const getLoginTitle = () => {
        switch (userType) {
            case 'Admin':
                return `Login as Admin`;
            case 'Operator':
                return `Login as Operator`;
            default:
                return `Create an Administrator`;
        }
    };

    const setActionSubmit = () => {
        switch (userType) {
            case 'Admin':
                handleAdminLogin();
                break;
            case 'Operator':
                handleOperatorLogin();
                break;
            default:
                handlecreateAdmin().then();
        }
    };
    const handleAdminLogin = () => {
        data['user_type'] = "1"
        if (username.trim() === '') {
            // Display an error message if the username is empty
            setErrorMessage('Username cannot be empty');
        } else {
            // Clear any previous error message
            setErrorMessage('');
            axios
                .post('http://localhost:8000/api/user-login/', JSON.stringify(data), config)
                .then((response) => {
                    console.log(response.data);
                    saveLocally({"username": username, 'user_type': "1", 'id': response.data.id})
                    navigate("admin");
                    // Handle data
                })
                .catch((error) => {
                    console.log(error);
                })
            //   this.props.history.push(AdminAccess());

            // Handle login logic here (e.g., send username to the server)
            console.log(`Logging in as ${userType} with username: ${username}`);
        }
    };

    const saveLocally = (resp) => {
        localStorage.setItem('username', resp.username);
        localStorage.setItem('user_type', resp.user_type);
        localStorage.setItem('id', resp.id);
        if ('admin_id' in resp) {
            localStorage.setItem('admin_id', resp.admin_id);
        }
    };
    const handleOperatorLogin = () => {
        data['user_type'] = "2"
        if (username.trim() === '') {
            // Display an error message if the username is empty
            setErrorMessage('Username cannot be empty');
        } else {
            // Clear any previous error message
            setErrorMessage('');
            axios
                .post('http://localhost:8000/api/user-login/', JSON.stringify(data), config)
                .then((response) => {
                    saveLocally(response.data)
                    navigate("gateway");
                    // Handle data
                })
                .catch((error) => {
                    console.log(error);
                })

            // Handle login logic here (e.g., send username to the server)
            console.log(`Logging in as ${userType} with username: ${username}`);
        }
    };
    const handlecreateAdmin = async () => {


        axios
            .post('http://localhost:8000/api/create-admin/', JSON.stringify(data), config)
            .then((response) => {
                console.log(response.data);
                saveLocally({"username": username, 'user_type': "1", 'id': response.data.id})
                navigate("admin");
                // Handle data
            })
            .catch((error) => {
                console.log(error);
            })
    };
    return (
        <div className="login-form">
            <h2>{geth2title()}</h2>
            <input
                type="text"
                placeholder={getPlc()}
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="text-input"
            />
            <button onClick={setActionSubmit}>{getLoginTitle()}</button>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
}


function App() {
    return (
        <div className="center-container">
            <LoginForm userType="Admin"/>
            <LoginForm userType="Operator"/>
            <LoginForm userType="CreateAdmin"/>
        </div>
    );
}

export default App;

