import React, {useState} from 'react';
import {Link, useNavigate} from "react-router-dom";
import GridView from '../gridview.css'; // Import the GridView component
import { ListViewComponent } from '@syncfusion/ej2-react-lists';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 22,
  },
  item: {
    padding: 10,
    fontSize: 18,
    height: 44,
  },
});

const FlatListBasics = () => {
  return (
    <View style={styles.container}>
      <FlatList
        data={[
          {key: 'Devin'},
          {key: 'Dan'},
          {key: 'Dominic'},
          {key: 'Jackson'},
          {key: 'James'},
          {key: 'Joel'},
          {key: 'John'},
          {key: 'Jillian'},
          {key: 'Jimmy'},
          {key: 'Julie'},
        ]}
        renderItem={({item}) => <Text style={styles.item}>{item.key}</Text>}
      />
    </View>
  );
};

const data = [
  { id: 1, name: 'Item 1', description: 'Description 1' },
  { id: 2, name: 'Item 2', description: 'Description 2' },
  // Add more items as needed
];
function AdminControl({ userType }) {
    const [username, setUsername] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    const submit = () => {
        if (username.trim() === '') {
            // Display an error message if the username is empty
            setErrorMessage(`${userType} fields cannot be empty`);
        } else {
            // Clear any previous error message
            setErrorMessage('');
            //   this.props.history.push(AdminAccess());

            // Handle login logic here (e.g., send username to the server)
            console.log(`Logging in as ${userType.value} with username: ${username}`);
        }
    };

    const getLoginTitle = () => {
        switch (userType) {
            case 'Gateway':
                return 'Delete the Gateway?';
            default:
                return 'Create an operator';
        }
    };

    const geth2title = () => {
        console.log(userType + "Selected")
        switch (userType) {
            case 'Gateway':
                return `Gateway Deletion`;
            case 'Operator':
                return `Operator Deletion`;
            default:
                return `Operator Creation`;
        }
    };
    return (
        <div className="login-form">
            <h2>{geth2title()}</h2>
            <input
                type="text"
                placeholder="${userType} name"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="text-input"
            />
            <button onClick={submit}>{getLoginTitle()}</button>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
}

function AdminAccess() {
    return (
        <div className="center-container">
            <AdminControl userType="Operator" />
            <p>eggs</p>
            <AdminControl userType='Gateway' />
                      <ListViewComponent id="list" dataSource={data} />
        </div>
    );
}

export default AdminAccess;
