import logo from './logo.svg';
import './App.css';

import React, {useEffect, useState} from "react";

import {BrowserRouter as Router, Switch, Route, useNavigate} from 'react-router-dom';
import axios from "axios";

function LoginForm({userType}) {
    const [username, setUsername] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    const getPlc = () => {
        switch (userType) {
            case 'Admin':
                return `Admin Username`;
            case 'Operator':
                return `Operator Username`;
            default:
                return `Administrator`;
        }
    };

    const geth2title = () => {
        switch (userType) {
            case 'Admin':
                return `Administrator Login`;
            case 'Operator':
                return `Operator Login`;
            default:
                return `Create`;
        }
    };
    const getLoginTitle = () => {
        switch (userType) {
            case 'Admin':
                return `Login as Admin`;
            case 'Operator':
                return `Login as Operator`;
            default:
                return `Create an Administrator`;
        }
    };

    const setActionSubmit = () => {
        switch (userType) {
            case 'Admin':
                handleAdminLogin();
                break;
            case 'Operator':
                handleOperatorLogin();
                break;
            default:
                handlecreateAdmin().then();
        }
    };
    const handleAdminLogin = () => {
        if (username.trim() === '') {
            // Display an error message if the username is empty
            setErrorMessage('Username cannot be empty');
        } else {
            // Clear any previous error message
            setErrorMessage('');
            localStorage.setItem('username', username);
            localStorage.setItem('user_type', "1");
            navigate("admin");
            //   this.props.history.push(AdminAccess());

            // Handle login logic here (e.g., send username to the server)
            console.log(`Logging in as ${userType} with username: ${username}`);
        }
    };

    const handleOperatorLogin = () => {
        if (username.trim() === '') {
            // Display an error message if the username is empty
            setErrorMessage('Username cannot be empty');
        } else {
            // Clear any previous error message
            setErrorMessage('');
            localStorage.setItem('username', username);
            localStorage.setItem('user_type', "2");
            navigate("admin");
            //   this.props.history.push(AdminAccess());

            // Handle login logic here (e.g., send username to the server)
            console.log(`Logging in as ${userType} with username: ${username}`);
        }
    };
    const handlecreateAdmin = async () => {
        const data = {
            username: username
        };

        const config = {
            headers: {
                "Content-Type": "application/json" // Use standard JSON content type
            }
        };

        axios
            .post('http://localhost:8000/api/create-admin/', JSON.stringify(data), config)
            .then((response) => {
                console.log(response.data);
                localStorage.setItem('username', username);
                localStorage.setItem('user_type', "1");
                navigate("admin");
                // Handle data
            })
            .catch((error) => {
                console.log(error);
            })
        // fetch('http://localhost:8000/api/create-admin/', {
        //     method: 'POST',
        //     headers: {
        //         'Content-Type': 'application/json,charset=UTF-8',
        //     },
        //     body: JSON.stringify(data),
        // })
        //     .then((response) => {
        //         return response.json(); // Parse the JSON response
        //     })
        //     .then((data) => {
        //         console.log(data);
        //         // setResponseMessage(data.message);
        //         navigate("admin");
        //     })
        //     .catch((errorResp) => {
        //         console.error('Error:', errorResp);
        //         alert(`Something went wrong: ${errorResp.value}`)
        //     });
    };
    return (
        <div className="login-form">
            <h2>{geth2title()}</h2>
            <input
                type="text"
                placeholder={getPlc()}
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="text-input"
            />
            <button onClick={setActionSubmit}>{getLoginTitle()}</button>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
}


function App() {
    return (
        <div className="center-container">
            <LoginForm userType="Admin"/>
            <LoginForm userType="Operator"/>
            <LoginForm userType="CreateAdmin"/>
        </div>
    );
}

export default App;

