import logo from './logo.svg';
import './App.css';

import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import AdminAccess from './components/admin_control';
import { useHistory } from 'react-router-dom'; // Import useHistory

function LoginForm({ userType }) {
  const [username, setUsername] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();

  const handleLogin = () => {
    if (username.trim() === '') {
      // Display an error message if the username is empty
      setErrorMessage('Username cannot be empty');
    } else {
      // Clear any previous error message
      setErrorMessage('');
      history.push('/AdminAccess');

      // Handle login logic here (e.g., send username to the server)
      alert(`Logging in as ${userType} with username: ${username}`);
    }
  };

   const getPlc = () => {
    switch (userType) {
      case 'Admin':
        return `Admin Username`;
      case 'Operator':
        return `Operator Username`;
      default:
        return `Administrator`;
    }
  };

   const geth2title = () => {
    switch (userType) {
      case 'Admin':
        return `Administrator Login`;
      case 'Operator':
        return `Operator Login`;
      default:
        return `Create`;
    }
  };
   const getLoginTitle = () => {
    switch (userType) {
      case 'Admin':
        return `Login as Admin`;
      case 'Operator':
        return `Login as Operator`;
      default:
        return `Create an Administrator`;
    }
  };


  return (
    <div className="login-form">
      <h2>{geth2title()}</h2>
      <input
        type="text"
        placeholder={getPlc()}
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        className="text-input"
      />
      <button onClick={handleLogin}>{getLoginTitle()}</button>
              {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
}

  const handleCreateAdmin = () => {
    // Handle login logic here (e.g., send username to the server)
    alert(`Create admin`);
  };
function App() {
  return (
    <div className="center-container">
      <LoginForm userType="Admin" />
      <LoginForm userType="Operator" />
        <LoginForm userType="CreateAdmin" />
    </div>
  );
}

export default App;

